; copyextern printfsection .text    global mainmain:    ;push 3    ;call label    push ($+7)    jmp label    nop    nop    nop    mov rdi, string    mov rsi, 70    mov rax, 0    call printf    retlabel:    pop rsi    push rsi ; peek the stack, store in rsi (the integer printed)    mov rdi, string    mov rax, 0    call printf    retsection .data    string: db "%p", 10, 0